DROP TABLE IF EXISTS `weapon`;
CREATE TABLE `weapon` (
  `item_id` int(10) unsigned NOT NULL default '0',
  `name` varchar(70) NOT NULL default '',
  `bodypart` enum('hatchling','lhand','lrhand','rhand','strider','wolf') NOT NULL,
  `crystallizable` enum('true','false') NOT NULL,
  `weight` smallint(5) unsigned NOT NULL default '0',
  `soulshots` tinyint(3) unsigned NOT NULL default '0',
  `spiritshots` tinyint(3) unsigned NOT NULL default '0',
  `material` enum('adamantaite','blood_steel','bone','bronze','chrysolite','cloth','crystal','damascus','fine_steel','horn','leather','mithril','oriharukon','paper','steel','wood') NOT NULL,
  `crystal_type` enum('a','b','c','d','s','none') NOT NULL,
  `p_dam` smallint(5) unsigned NOT NULL default '0',
  `rnd_dam` tinyint(3) unsigned NOT NULL default '0',
  `weaponType` enum('bigblunt','bigsword','blunt','bow','dagger','dual','dualfist','etc','fist','none','pet','pole','rod','sword') NOT NULL,
  `critical` tinyint(3) unsigned NOT NULL default '0',
  `hit_modify` tinyint(4) NOT NULL default '0',
  `avoid_modify` tinyint(4) NOT NULL default '0',
  `shield_def` smallint(5) unsigned NOT NULL default '0',
  `shield_def_rate` tinyint(3) unsigned NOT NULL default '0',
  `atk_speed` smallint(5) unsigned NOT NULL default '0',
  `mp_consume` tinyint(3) unsigned NOT NULL default '0',
  `m_dam` tinyint(3) unsigned NOT NULL default '0',
  `duration` smallint(6) NOT NULL default '0',
  `price` int(10) unsigned NOT NULL default '0',
  `crystal_count` smallint(5) unsigned NOT NULL default '0',
  `sellable` enum('true','false') NOT NULL,
  `dropable` enum('true','false') NOT NULL,
  `destroyable` enum('true','false') NOT NULL,
  `tradeable` enum('true','false') NOT NULL,
  `item_skill_id` smallint(5) unsigned NOT NULL default '0',
  `item_skill_lvl` smallint(5) unsigned NOT NULL default '0',
  `enchant4_skill_id` smallint(5) unsigned NOT NULL default '0',
  `enchant4_skill_lvl` smallint(5) unsigned NOT NULL default '0',
  `onCast_skill_id` smallint(5) unsigned NOT NULL default '0',
  `onCast_skill_lvl` smallint(5) unsigned NOT NULL default '0',
  `onCast_skill_chance` tinyint(3) unsigned NOT NULL default '0',
  `onCrit_skill_id` smallint(5) unsigned NOT NULL default '0',
  `onCrit_skill_lvl` smallint(5) unsigned NOT NULL default '0',
  `onCrit_skill_chance` tinyint(3) unsigned NOT NULL default '0',
  `icon` varchar(123) NOT NULL default 'Icon.NOIMAGE',
  PRIMARY KEY  (`item_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

